import React, { useState, useEffect } from "react";
import axios from "axios";

export default function CCFeatures() {
  const [CCData, setCCData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    fetchCCData();
  }, []);

  const fetchCCData = async () => {
    const data = await axios
      .get("https://mocki.io/v1/b9c63035-97c5-40a0-b45c-2abdf5261bdf")
      .then((res) => res.data)
      .catch((err) => {
        console.log(err);
        setError(true);
      });

    setCCData(data);
    setLoading(false);
    console.log(data);
  };

  return (
    <div className="cc-features-container">
      <h1>Experience The Cardo</h1>

      {error ? (
        <p>There was an error aquiring the data</p>
      ) : loading ? (
        <div className="loader"></div>
      ) : (
        CCData.slice(0, 6).map((feature) => {
          const { title, description, button } = feature;
          return (
            <div>
              <h3>{title}</h3>
              <p>{description}</p>
              <a href={button.link}>{button.text}</a>
            </div>
          );
        })
      )}
    </div>
  );
}
